ruleContent.content:
@js:
doc = org.jsoup.Jsoup.parse(result)

name = doc.select("div.title span.name")

time = doc.select("div.info_col")[0].text().match(/(\d{4})-(\d{2})-\d{2}/)

year = time[1]

month = time[2]

//java.log(year)

num = name.size()

checkUrl = 'https://www.wnacg.com'+ doc.select("div.grid li a")[0].attr("href")
checkDoc = org.jsoup.Jsoup.parse(java.ajax(checkUrl))
checkId = checkDoc.select("#picarea").attr("src").match(/\/\/(\w+?).wnacg/)[1]

//java.log(checkId)

if(!name[0].text().match(/[\u4e00-\u9fa5]+/g)&&(year>=2013&&month>=3)){

imgs = doc.select("div.grid li img")

id = imgs[0].attr("src").match(/\/[\d]{4,6}\/[\d]{2,8}\//g)[0]

a = '<img src = \"https://'+checkId+'.wnacg.com/data'+id

b = '.jpg\">'

list = []

for(i=0;i<num;i++){
list[i] = a+name[i].text()+b
}
result = list.join("")
}else{

imgs = doc.select("div.cc li img")

list = []
for(i in imgs){
path = imgs[i].attr("src").match(/\/[\d]{4,6}\/[\d]{2,8}\/[^"]+/g)[0]
list[i]= '<img src = \"https://'+checkId+'.wnacg.com/data'+path+ '\">'
}
result = list.join("")
}

ruleToc.chapterList:
class.f_left paginator&&link[rel*=alternate]@js:

doc = org.jsoup.Jsoup.parse(result)
//java.log(doc)
size = doc.select("span").size()

switch(size) {
     case 1:
        link = doc.select("link").attr("href")
        id = link.match(/aid-(\d+)/)[1]
        //java.log(id)
        list = [] 
        list [0] = '<a href = \"https://www.wnacg.com/photos-index-aid-'+id+'.html\"></a>'
        result = list
        break;
     default:
        element = doc.select("a")[doc.select("a").size()-2]
        num = Number(element.text())
        path = element.attr("href")
        id = path.match(/aid-(\d+)/)[1]
        url = '<a href = \"https://www.wnacg.com/'
        list = []
        for(i=1;i<=num;i++){
list.push(url+"photos-index-page-"+i+"-aid-"+id+'.html\">'+i+'</a>')
}
        result = list
}

