code='#'+text.substring(12,22)+'#'
text=obj[code]
if(!text){
text=code;
//java.log("æœªæ”¶å½•"+code);
un.push(code);
}
return text
})
if(un.length!=0){
body=un;
//java.log(un)
post={
    method:"POST",
    body:String(body),
    };
java.ajax(server+"," + JSON.stringify(post))}
</js>

